import React, { Component } from 'react';

import { logIn } from '../../services/LoginService';
import StyledTextField from '../shared/StyledTextField/StyledTextField';
import Button from '../shared/Button/Button';

import classes from './LogInModal.module.css';

class LogInModal extends Component {
    constructor(props) {
        super();
        this.state = {
            open: false,
            email: null,
            password: null
        }

        this.LogInUser = this.LogInUser.bind(this);
    }

    openModal = () => {
        this.setState({ open: true });
    }

    closeModal = () => {
        this.setState({ open: false });
    }

    LogInUser = () => {
        this.openModal();
    }

    startNewSession = () => {

        const data = {
            email: this.state.email,
            password: this.state.comment,
        }
        logIn(data)
            .then(data => {
                // send email
                this.closeModal();
            })
            .catch(e => {
                // add UX things / form validation
                alert("Please fix any errors", e);
                this.closeModal();
                console.log(e)
            });
    }

    render() {
        return (
            <div className={classes.lim}>
                <p>Log in to view your profile</p>
                <StyledTextField content={"Email"} />
                <StyledTextField content={"Password"} />
                <Button id="signUpButton" content='Forgot Password' inverted />

                <div className={classes.footer}>
                    <Button id="signUpButton" content='Sign Up' inverted />
                    <Button content='Log In' click={this.startNewSession} />
                </div>
            </div>
        )
    }
}

export default LogInModal;
